/**
 * FleetX Dashboard - Main JavaScript
 */

// ============== GLOBALS ==============
let map;
let vehicleMarkers = {};
let vehiclesData = [];
let vehiclePreviousPositions = {}; // Store previous positions for bearing calculation
let geofenceLayers = {};
let routePolyline = null;
let routeMarker = null;
let routeData = [];
let playbackIndex = 0;
let playbackInterval = null;
let isPlaying = false;
let selectedVehicleId = null;
let callerMarker = null;
let callerMode = false;
let drawControl = null;
let drawnItems;
let buildingLabels = {};
let buildingsData = [];

// ============== INITIALIZATION ==============
document.addEventListener('DOMContentLoaded', () => {
    initMap();
    loadVehicles();
    loadGeofences();
    loadOverviewStats();
    loadBuildings();  // Add this line
    setupEventListeners();

    // Refresh data periodically
    setInterval(() => {
        loadVehicles();
        if (selectedVehicleId) {
            loadDispatchRankings();
        }
    }, 30000);
});

function initMap() {
    // Define India bounds (Southwest corner, Northeast corner)
    const indiaBounds = L.latLngBounds(
        [6.4, 68.1],    // Southwest: southernmost and westernmost point
        [35.5, 97.4]    // Northeast: northernmost and easternmost point
    );

    // Initialize map centered on India with restrictions
    map = L.map('map', {
        zoomControl: false,
        minZoom: 4,                    // Prevent zooming out below level 4
        maxBounds: indiaBounds,        // Restrict panning to India region
        maxBoundsViscosity: 1.0        // Hard lock - prevents dragging outside bounds
    }).setView([20.5937, 78.9629], 5);

    // Add minimal tile layer
    L.tileLayer('https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>',
        maxZoom: 19
    }).addTo(map);

    // Add zoom control to bottom right
    L.control.zoom({ position: 'bottomright' }).addTo(map);

    // Initialize draw layer for geofences
    drawnItems = new L.FeatureGroup();
    map.addLayer(drawnItems);

    // Map click handler for caller location
    map.on('click', (e) => {
        if (callerMode) {
            setCallerLocation(e.latlng.lat, e.latlng.lng);
            callerMode = false;
            document.getElementById('caller-mode-btn').classList.remove('bg-gray-900', 'text-white');
        }
    });

    // Draw event handlers - use string event name for compatibility
    map.on('draw:created', function(e) {
        const layer = e.layer;
        drawnItems.addLayer(layer);
        openGeofenceModal(layer);
    });

    // Update markers when zoom changes
    map.on('zoomend', () => {
        updateVehicleMarkers();
        updateBuildingLabels();  // Add this line

    });
}

function setupEventListeners() {
    // Geofence form submit
    document.getElementById('geofence-form').addEventListener('submit', saveGeofence);

    // Keyboard shortcuts
    document.addEventListener('keydown', (e) => {
        if (e.key === 'Escape') {
            closeAllPanels();
        }
        if (e.key === ' ' && routeData.length > 0) {
            e.preventDefault();
            playbackControl('toggle');
        }
    });
}

// ============== VEHICLES ==============
async function loadVehicles() {
    try {
        const response = await fetch('/api/vehicles');
        vehiclesData = await response.json();
        updateVehicleMarkers();
        renderVehicleList();
        updateStatusCounts();
        populateVehicleSelects();
    } catch (error) {
        console.error('Failed to load vehicles:', error);
    }
}

/**
 * Calculate bearing (direction angle) between two geographic points
 *
 * @param {number} lat1 - Previous latitude
 * @param {number} lon1 - Previous longitude
 * @param {number} lat2 - Current latitude
 * @param {number} lon2 - Current longitude
 * @returns {number} Bearing in degrees (0-360), where 0° = North, 90° = East, 180° = South, 270° = West
 *
 * Formula explanation:
 * - Converts lat/lng to radians
 * - Uses spherical trigonometry to calculate bearing
 * - Normalizes result to 0-360° range
 */
function calculateBearing(lat1, lon1, lat2, lon2) {
    // Convert degrees to radians
    const φ1 = lat1 * Math.PI / 180;
    const φ2 = lat2 * Math.PI / 180;
    const Δλ = (lon2 - lon1) * Math.PI / 180;

    // Calculate bearing using spherical law of cosines
    const y = Math.sin(Δλ) * Math.cos(φ2);
    const x = Math.cos(φ1) * Math.sin(φ2) -
              Math.sin(φ1) * Math.cos(φ2) * Math.cos(Δλ);

    // Convert to degrees and normalize to 0-360
    const θ = Math.atan2(y, x);
    const bearing = (θ * 180 / Math.PI + 360) % 360;

    return bearing;
}

