// ============== ROUTE PLAYBACK ==============
async function loadRoute() {
    const date = document.getElementById('playback-date').value;
    if (!selectedVehicleId || !date) return;

    try {
        const response = await fetch(`/api/vehicles/${selectedVehicleId}/route?start=${date}&end=${date}T23:59:59`);
        let rawData = await response.json();

        if (rawData.length === 0) {
            document.getElementById('playback-info').textContent = 'No route data for this date';
            return;
        }

        // Clear previous route
        clearRoute();

        // Filter out invalid coordinates
        routeData = rawData.filter(p =>
            p.lat !== null && p.lng !== null &&
            p.lat !== undefined && p.lng !== undefined &&
            !isNaN(p.lat) && !isNaN(p.lng) &&
            Math.abs(p.lat) <= 90 && Math.abs(p.lng) <= 180
        );

        if (routeData.length === 0) {
            document.getElementById('playback-info').textContent = 'No valid coordinates in route data';
            return;
        }

        // Draw route polyline
        const latlngs = routeData.map(p => [p.lat, p.lng]);
        routePolyline = L.polyline(latlngs, {
            color: '#111827',
            weight: 2,
            opacity: 0.6,
            dashArray: '5, 10'
        }).addTo(map);

        // Fit map to route only if bounds are valid
        const bounds = routePolyline.getBounds();
        if (bounds && bounds.isValid()) {
            map.fitBounds(bounds, { padding: [50, 50] });
        } else if (latlngs.length > 0) {
            // Fallback: center on first point
            map.setView(latlngs[0], 14);
        }

        // Create playback marker with vehicle icon
        // Calculate initial bearing from first two points if available
        let initialBearing = routeData[0]?.bearing || 0;
        if (routeData.length > 1 && routeData[0] && routeData[1]) {
            const point1 = routeData[0];
            const point2 = routeData[1];
            if (point1.lat !== point2.lat || point1.lng !== point2.lng) {
                initialBearing = calculateBearing(point1.lat, point1.lng, point2.lat, point2.lng);
            }
        }

        const playbackVehicle = {
            id: selectedVehicleId,
            status: routeData[0]?.status || 'UNKNOWN',
            bearing: initialBearing
        };
        const playbackIcon = createVehicleIcon(playbackVehicle, map.getZoom());
        routeMarker = L.marker(latlngs[0], { icon: playbackIcon }).addTo(map);

        playbackIndex = 0;
        updatePlaybackUI();

        document.getElementById('playback-slider').max = routeData.length - 1;
        document.getElementById('playback-info').textContent = `${routeData.length} valid points loaded`;

    } catch (error) {
        console.error('Failed to load route:', error);
        document.getElementById('playback-info').textContent = 'Error loading route';
    }
}

function clearRoute() {
    if (routePolyline) {
        map.removeLayer(routePolyline);
        routePolyline = null;
    }
    if (routeMarker) {
        map.removeLayer(routeMarker);
        routeMarker = null;
    }
    routeData = [];
    playbackIndex = 0;
    stopPlayback();
}

function playbackControl(action) {
    switch (action) {
        case 'toggle':
            isPlaying ? stopPlayback() : startPlayback();
            break;
        case 'next':
            if (playbackIndex < routeData.length - 1) {
                playbackIndex++;
                updatePlaybackPosition();
            }
            break;
        case 'prev':
            if (playbackIndex > 0) {
                playbackIndex--;
                updatePlaybackPosition();
            }
            break;
    }
}

function startPlayback() {
    if (routeData.length === 0) return;
    isPlaying = true;
    updatePlayButton();

    playbackInterval = setInterval(() => {
        if (playbackIndex < routeData.length - 1) {
            playbackIndex++;
            updatePlaybackPosition();
        } else {
            stopPlayback();
        }
    }, 200);
}

function stopPlayback() {
    isPlaying = false;
    updatePlayButton();
    if (playbackInterval) {
        clearInterval(playbackInterval);
        playbackInterval = null;
    }
}

function updatePlayButton() {
    const btn = document.getElementById('play-btn');
    btn.innerHTML = isPlaying
        ? '<svg class="w-5 h-5" fill="currentColor" viewBox="0 0 24 24"><path d="M6 4h4v16H6V4zm8 0h4v16h-4V4z"/></svg>'
        : '<svg class="w-5 h-5" fill="currentColor" viewBox="0 0 24 24"><path d="M8 5v14l11-7z"/></svg>';
}

function seekPlayback(value) {
    playbackIndex = parseInt(value);
    updatePlaybackPosition();
}

function updatePlaybackPosition() {
    if (!routeMarker || !routeData[playbackIndex]) return;

    const point = routeData[playbackIndex];
    routeMarker.setLatLng([point.lat, point.lng]);

    // Calculate bearing from movement direction during playback
    let calculatedBearing = point.bearing || 0; // Default to backend bearing

    if (playbackIndex > 0) {
        // Calculate bearing from previous point to current point
        const prevPoint = routeData[playbackIndex - 1];
        if (prevPoint && (prevPoint.lat !== point.lat || prevPoint.lng !== point.lng)) {
            calculatedBearing = calculateBearing(
                prevPoint.lat,
                prevPoint.lng,
                point.lat,
                point.lng
            );
        }
    }

    // Update icon with current point's bearing and status
    const playbackVehicle = {
        id: selectedVehicleId,
        status: point.status || 'UNKNOWN',
        bearing: calculatedBearing
    };
    const updatedIcon = createVehicleIcon(playbackVehicle, map.getZoom());
    routeMarker.setIcon(updatedIcon);

    updatePlaybackUI();
}

function updatePlaybackUI() {
    if (!routeData[playbackIndex]) return;

    const point = routeData[playbackIndex];
    document.getElementById('playback-slider').value = playbackIndex;

    const time = point.timestamp ? new Date(point.timestamp).toLocaleTimeString('en-US', {
        hour: '2-digit', minute: '2-digit'
    }) : '--:--';
    document.getElementById('playback-time').textContent = time;

    document.getElementById('playback-info').innerHTML = `
        <span class="inline-block w-2 h-2 rounded-full mr-1" style="background: ${getStatusColor(point.status)}"></span>
        ${point.speed || 0} km/h · ${point.address || 'Unknown location'}
    `;
}

